    return this._amount;
  }

  set amount(n) {
    this._amount = n;
  }

  get currency() {
    return this._currency;
  }

  set currency(n) {
    this._currency = n;
  }

  displayFullPrice() {
    return `${this._amount} ${this._currency.name} (${this._currency.code})`;
  }

  static convertPrice(amount, conversionRate) {
    return amount * conversionRate;
  }
}

